+import { useMemo, useState } from 'react';
+
+export const MAX_SELECTION = 6;
+export const MIN_NUMBER = 1;
+export const MAX_NUMBER = 45;
+
+export interface NumberSelectionState {
+  selectedNumbers: number[];
+  remainingSlots: number;
+  canSelectMore: boolean;
+  isComplete: boolean;
+  toggleNumber(value: number): void;
+  isSelected(value: number): boolean;
+  reset(): void;
+  setSelection(values: number[]): void;
+}
+
+export function useNumberSelection(): NumberSelectionState {
+  const [selectedNumbers, setSelectedNumbers] = useState<number[]>([]);
+
+  const derived = useMemo(() => {
+    const uniqueSorted = [...new Set(selectedNumbers)].sort((a, b) => a - b);
+    const limited = uniqueSorted.slice(0, MAX_SELECTION);
+    const remainingSlots = Math.max(0, MAX_SELECTION - limited.length);
+    return {
+      selectedNumbers: limited,
+      remainingSlots,
+      canSelectMore: remainingSlots > 0,
+      isComplete: remainingSlots === 0
+    };
+  }, [selectedNumbers]);
+
+  function toggleNumber(value: number): void {
+    setSelectedNumbers((current) => {
+      if (current.includes(value)) {
+        return current.filter((num) => num !== value);
+      }
+      if (current.length >= MAX_SELECTION) {
+        return current;
+      }
+      if (value < MIN_NUMBER || value > MAX_NUMBER) {
+        return current;
+      }
+      return [...current, value];
+    });
+  }
+
+  function isSelected(value: number): boolean {
+    return derived.selectedNumbers.includes(value);
+  }
+
+  function reset(): void {
+    setSelectedNumbers([]);
+  }
+
+  function setSelection(values: number[]): void {
+    setSelectedNumbers(values.filter((value) => value >= MIN_NUMBER && value <= MAX_NUMBER));
+  }
+
+  return {
+    selectedNumbers: derived.selectedNumbers,
+    remainingSlots: derived.remainingSlots,
+    canSelectMore: derived.canSelectMore,
+    isComplete: derived.isComplete,
+    toggleNumber,
+    isSelected,
+    reset,
+    setSelection
+  };
+}
