+import type { NumberSelectionState } from '../../hooks/useNumberSelection';
+
+interface NumberGridProps {
+  selection: Pick<NumberSelectionState, 'selectedNumbers' | 'toggleNumber' | 'isSelected' | 'canSelectMore'>;
+  disabled?: boolean;
+  minNumber?: number;
+  maxNumber?: number;
+}
+
+function buildNumbers(min: number, max: number): number[] {
+  return Array.from({ length: max - min + 1 }, (_, index) => min + index);
+}
+
+export function NumberGrid({
+  selection,
+  disabled = false,
+  minNumber = 1,
+  maxNumber = 45
+}: NumberGridProps): JSX.Element {
+  const numbers = buildNumbers(minNumber, maxNumber);
+
+  return (
+    <div className="number-grid" role="grid" aria-label="Select six unique numbers">
+      {numbers.map((value) => {
+        const isSelected = selection.isSelected(value);
+        const isLocked = disabled || (!isSelected && !selection.canSelectMore);
+        return (
+          <button
+            key={value}
+            type="button"
+            role="gridcell"
+            className={`number-grid__button${isSelected ? ' is-selected' : ''}${isLocked ? ' is-locked' : ''}`}
+            aria-pressed={isSelected}
+            aria-disabled={isLocked}
+            disabled={disabled}
+            onClick={() => {
+              if (isLocked) {
+                return;
+              }
+              selection.toggleNumber(value);
+            }}
+            data-testid={`number-button-${value}`}
+          >
+            <span aria-hidden="true">{value}</span>
+            <span className="sr-only">{`Number ${value}`}</span>
+          </button>
+        );
+      })}
+    </div>
+  );
+}
+
+export default NumberGrid;
