{"output":"     1	import { push, set, type Database } from 'firebase/database';
     2	import { LOTTO_MAX_NUMBER, LOTTO_MIN_NUMBER, LOTTO_SELECTION_SIZE } from '../constants/lotto';
     3	import { getRealtimeDatabase, getSessionKey, getTicketsRef } from './firebaseClient';
     4	import { logger } from './logger';
     5	
     6	const log = logger.scoped('issue');
     7	
     8	export type TicketSource = 'manual' | 'auto';
     9	
    10	export interface IssueTicketInput {
    11	  name: string;
    12	  numbers: number[];
    13	  source?: TicketSource;
    14	  clientId?: string;
    15	  metadata?: Record<string, unknown>;
    16	  sessionKey?: string;
    17	  database?: Database;
    18	}
    19	
    20	export interface IssueTicketResult {
    21	  ticketId: string;
    22	}
    23	
    24	function sanitizeName(name: string): string {
    25	  return name?.trim() ?? '';
    26	}
    27	
    28	function normalizeNumbers(numbers: number[]): number[] {
    29	  const filtered = numbers
    30	    .map((value) => Number(value))
    31	    .filter(
    32	      (value) => Number.isInteger(value) && value >= LOTTO_MIN_NUMBER && value <= LOTTO_MAX_NUMBER
    33	    );
    34	  const unique = Array.from(new Set(filtered)).sort((a, b) => a - b);
    35	
    36	  if (unique.length !== LOTTO_SELECTION_SIZE) {
    37	    throw new Error(`[ticketService] Ticket must contain ${LOTTO_SELECTION_SIZE} unique numbers`);
    38	  }
    39	
    40	  return unique;
    41	}
    42	
    43	export async function issueTicket(input: IssueTicketInput): Promise<IssueTicketResult> {
    44	  const database = input.database ?? getRealtimeDatabase();
    45	  const sessionKey = input.sessionKey ?? getSessionKey();
    46	  const sanitizedName = sanitizeName(input.name);
    47	
    48	  if (!sanitizedName) {
    49	    throw new Error('[ticketService] Player name is required');
    50	  }
    51	
    52	  const numbers = normalizeNumbers(input.numbers);
    53	  const ticketRef = push(getTicketsRef(database, sessionKey));
    54	  const ticketId = ticketRef.key;
    55	
    56	  if (!ticketId) {
    57	    throw new Error('[ticketService] Failed to allocate ticket id');
    58	  }
    59	
    60	  const metadata = {
    61	    ...(input.metadata ?? {}),
    62	    ...(input.clientId ? { clientId: input.clientId } : {})
    63	  };
    64	
    65	  log.info('start', {
    66	    payload: {
    67	      sessionKey,
    68	      name: sanitizedName,
    69	      source: input.source ?? 'manual'
    70	    }
    71	  });
    72	
    73	  const ticketPayload: Record<string, unknown> = {
    74	    name: sanitizedName,
    75	    numbers,
    76	    matchedNumbers: [],
    77	    createdAt: new Date().toISOString(),
    78	    source: input.source ?? 'manual'
    79	  };
    80	
    81	  if (Object.keys(metadata).length > 0) {
    82	    ticketPayload.metadata = metadata;
    83	  }
    84	
    85	  try {
    86	    await set(ticketRef, ticketPayload);
    87	    log.info('success', {
    88	      payload: {
    89	        ticketId,
    90	        sessionKey
    91	      }
    92	    });
    93	    return { ticketId };
    94	  } catch (error) {
    95	    log.error('error', {
    96	      error,
    97	      payload: {
    98	        sessionKey,
    99	        ticketId
   100	      }
   101	    });
   102	    throw error;
   103	  }
   104	}
