    79	}
    80	
    81	function arraysEqual(first?: number[], second?: number[]): boolean {
    82	  const a = first ?? [];
    83	  const b = second ?? [];
    84	
    85	  if (a.length !== b.length) {
    86	    return false;
    87	  }
    88	
    89	  for (let index = 0; index < a.length; index += 1) {
    90	    if (a[index] !== b[index]) {
    91	      return false;
    92	    }
    93	  }
    94	
    95	  return true;
    96	}
    97	
    98	function stableSerialize(value: unknown): string {
    99	  if (Array.isArray(value)) {
   100	    return `[${value.map((entry) => stableSerialize(entry)).join(',')}]`;
   101	  }
   102	
   103	  if (value && typeof value === 'object') {
   104	    const entries = Object.entries(value as Record<string, unknown>)
   105	      .sort(([a], [b]) => a.localeCompare(b))
   106	      .map(([key, val]) => `${key}:${stableSerialize(val)}`);
   107	    return `{${entries.join(',')}}`;
   108	  }
   109	
   110	  return JSON.stringify(value ?? null);
   111	}
   112	
   113	function areTicketsEqual(previous?: TicketRecord, next?: TicketRecord): boolean {
   114	  if (!previous || !next) {
   115	    return false;
   116	  }
   117	
   118	  if (previous === next) {
   119	    return true;
   120	  }
   121	
   122	  return (
   123	    previous.name === next.name &&
   124	    previous.createdAt === next.createdAt &&
   125	    previous.source === next.source &&
   126	    arraysEqual(previous.numbers, next.numbers) &&
   127	    arraysEqual(previous.matchedNumbers, next.matchedNumbers) &&
   128	    stableSerialize(previous.metadata ?? {}) === stableSerialize(next.metadata ?? {})
   129	  );
   130	}
   131	
   132	export function projectTickets(map: TicketsMap): TicketBoardEntry[] {
   133	  return Object.entries(map)
   134	    .map(([id, ticket]) => createTicketEntry(id, ticket))
   135	    .sort(sortTicketsDescending);
   136	}
   137	
   138	export function diffTicketMaps(previous: TicketsMap, next: TicketsMap): TicketBoardDiff {
   139	  const removed: string[] = [];
   140	  const added: TicketBoardEntry[] = [];
   141	  const updated: TicketBoardEntry[] = [];
   142	
   143	  const previousIds = new Set(Object.keys(previous));
   144	  const nextIds = new Set(Object.keys(next));
   145	
   146	  for (const id of previousIds) {
   147	    if (!nextIds.has(id)) {
   148	      removed.push(id);
   149	    }
   150	  }
   151	
   152	  for (const id of nextIds) {
   153	    if (!previousIds.has(id)) {
   154	      added.push(createTicketEntry(id, next[id]));
   155	      continue;
   156	    }
   157	
   158	    if (!areTicketsEqual(previous[id], next[id])) {
   159	      updated.push(createTicketEntry(id, next[id]));
   160	    }
   161	  }
   162	
   163	  added.sort(sortTicketsDescending);
   164	  updated.sort(sortTicketsDescending);
   165	
   166	  return {
   167	    added,
   168	    updated,
   169	    removed
   170	  };
   171	}
   172	
   173	export function subscribeToTicketBoard(options: TicketBoardSubscriptionOptions): Unsubscribe {
   174	  let previousMap: TicketsMap = {};
   175	
   176	  return subscribeToTickets({
   177	    sessionKey: options.sessionKey,
   178	    clientId: options.clientId,
   179	    onData: (nextMap) => {
   180	      const diff = diffTicketMaps(previousMap, nextMap);
   181	      previousMap = nextMap;
   182	      const tickets = projectTickets(nextMap);
   183	
   184	      options.onUpdate({
   185	        tickets,
   186	        total: tickets.length,
   187	        diff
   188	      });
   189	    },
   190	    onError: options.onError
   191	  });
   192	}
