{"output":"import { useEffect, useMemo, useState } from 'react';
import TicketList from '../components/draw/TicketList';
import { useSession } from '../context/SessionProvider';
import { subscribeToTicketBoard, type TicketBoardDiff, type TicketBoardEntry } from '../services/ticketSubscription';
import '../styles/draw-dashboard.css';

interface TicketBoardState {
  tickets: TicketBoardEntry[];
  total: number;
  diff: TicketBoardDiff | null;
  isLoading: boolean;
  error: unknown | null;
  lastUpdatedAt: string | null;
}

const INITIAL_STATE: TicketBoardState = {
  tickets: [],
  total: 0,
  diff: null,
  isLoading: true,
  error: null,
  lastUpdatedAt: null
};

function formatUpdatedAt(value: string | null): string {
  if (!value) {
    return '대기 중';
  }
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) {
    return '대기 중';
  }
  return new Intl.DateTimeFormat('ko-KR', {
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  }).format(date);
}

function DrawDashboardPage(): JSX.Element {
  const { sessionKey, clientId } = useSession();
  const [boardState, setBoardState] = useState<TicketBoardState>(INITIAL_STATE);

  useEffect(() => {
    setBoardState((previous) => ({ ...previous, isLoading: true, error: null }));

    const unsubscribe = subscribeToTicketBoard({
      sessionKey,
      clientId,
      onUpdate: (update) => {
        setBoardState({
          tickets: update.tickets,
          total: update.total,
          diff: update.diff,
          isLoading: false,
          error: null,
          lastUpdatedAt: new Date().toISOString()
        });
      },
      onError: (error) => {
        setBoardState((previous) => ({ ...previous, error, isLoading: false }));
      }
    });

    return () => {
      unsubscribe();
    };
  }, [sessionKey, clientId]);

  const diffSummary = useMemo(() => {
    return {
      added: boardState.diff?.added.length ?? 0,
      updated: boardState.diff?.updated.length ?? 0,
      removed: boardState.diff?.removed.length ?? 0
    };
  }, [boardState.diff]);

  return (
    <section className="draw-dashboard">
      <header className="draw-dashboard__header">
        <div>
          <p className="draw-dashboard__eyebrow">활성 세션</p>
          <h1>{sessionKey}</h1>
          <p className="draw-dashboard__subhead">발급 현황과 추첨 준비 상태를 한 화면에서 모니터링하세요.</p>
        </div>
        <div className="draw-dashboard__metrics">
          <div>
            <span>총 티켓</span>
            <strong data-testid="ticket-board-count">{boardState.total}</strong>
          </div>
          <div>
            <span>마지막 갱신</span>
            <strong>{formatUpdatedAt(boardState.lastUpdatedAt)}</strong>
          </div>
        </div>
      </header>

      <div className="draw-dashboard__layout">
        <section className="ticket-board" aria-labelledby="ticket-board-heading">
          <div className="ticket-board__heading">
            <div>
              <p className="ticket-board__eyebrow">실시간 티켓 스트림</p>
              <h2 id="ticket-board-heading" data-testid="ticket-board-heading">
                발급 티켓 보드
              </h2>
            </div>
            <ul className="ticket-board__diff" aria-label="최근 동기화 현황">
              <li>+{diffSummary.added} 신규</li>
              <li>{diffSummary.updated} 업데이트</li>
              <li>-{diffSummary.removed} 제거</li>
            </ul>
          </div>

          <TicketList tickets={boardState.tickets} isLoading={boardState.isLoading} error={boardState.error} />
        </section>

        <aside className="draw-dashboard__rail">
          <div className="draw-dashboard__panel">
            <h3>추첨 준비 상태</h3>
            <p className="draw-dashboard__panel-copy">
              룰렛 애니메이션과 추첨 로직은 User Story 3에서 연결됩니다. 현재는 보드 상태만 점검할 수 있습니다.
            </p>
            <ul className="draw-dashboard__status-list">
              <li>
                <span>룰렛</span>
                <strong>대기중</strong>
              </li>
              <li>
                <span>하이라이터</span>
                <strong>연동 준비</strong>
              </li>
              <li>
                <span>세션 초기화</span>
                <strong>보호됨</strong>
              </li>
            </ul>
          </div>

          <div className="draw-dashboard__panel">
            <h3>진행 제어</h3>
            <p className="draw-dashboard__panel-copy">추첨과 초기화 제어는 다음 스토리에서 활성화됩니다.</p>
            <div className="draw-dashboard__actions">
              <button type="button" disabled>
                추첨 시작
              </button>
              <button type="button" disabled>
                추첨 즉시 갱신
              </button>
              <button type="button" disabled>
                세션 초기화
              </button>
            </div>
          </div>
        </aside>
      </div>
    </section>
  );
}

export default DrawDashboardPage;
